GeoLocation.prototype.getLocation = function(callback){
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(function(position){
            var lat = position.coords.latitude;
            var lng = position.coords.longitude;

            var selectionGeoLocation = GeoLocation.prototype.getListSelectionGeoLocation(lat, lng);
            callback(selectionGeoLocation);
        }, function(error){
            switch(error.code) {
                case error.PERMISSION_DENIED:
                    console.log('User denied request geolocation');
                    GeoLocation.prototype.findFromIp(callback);
                    break;
                case error.POSITION_UNAVAILABLE:
                    console.log('Information geolocation unavailable');
                    GeoLocation.prototype.findFromIp(callback);
                    break;
                case error.TIMEOUT:
                    console.log('Request timeout');
                    GeoLocation.prototype.findFromIp(callback);
                    break;
                case error.UNKNOWN_ERROR:
                    console.log('Unknown Error');
                    GeoLocation.prototype.findFromIp(callback);
                    break;
            }
        }, {
            timeout:30000
        });
    } else { 
        GeoLocation.prototype.findFromIp(callback);
    }
}

GeoLocation.prototype.getListSelectionGeoLocation = function (lat, lng){
    var selectionGeoLocation = [];

    $.each(this.city, function(a, b){
        var distance = GeoLocation.prototype.getDistanceFromLatLng(lat, lng, b.latitude, b.longitude); // mencari jarak antara dua titik
        if(b.radius >= distance){ // membandingkan apakah termasuk di dalam radius kota tsb
            selectionGeoLocation.push(b.nama);
        }
    });
    
    return selectionGeoLocation;
}

GeoLocation.prototype.getDistanceFromLatLng = function(lat1,lon1,lat2,lon2) {
    var Rad = 6371; // Radius bumi di km
    var degLat = this.deg2rad(lat2-lat1);  //convert to degree radiant
    var degLng = this.deg2rad(lon2-lon1); //convert to degree radiant
    var a = Math.sin(degLat/2) * Math.sin(degLat/2) +
      Math.cos(this.deg2rad(lat1)) * Math.cos(this.deg2rad(lat2)) * 
      Math.sin(degLng/2) * Math.sin(degLng/2); 

    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
    var distKm = Rad * c; // jarak dalam satuan km
    var distMet = distKm * 1000; // jarak dalam satuan meter

    return Math.floor(distMet);
}

GeoLocation.prototype.deg2rad = function(deg) {
    return deg * (Math.PI/180)
}

GeoLocation.prototype.findFromIp = function(callback){
    $.getJSON('https://freegeoip.net/json/', function (location) {
        var lng = location.longitude;
        var lat = location.latitude;
        var selectionGeoLocation = GeoLocation.prototype.getListSelectionGeoLocation(lat, lng);
        
        if(selectionGeoLocation.length == 0){
            callback([location.city]);
        }else{
            callback(selectionGeoLocation);
        }
    }); // request latitude dan longitude berdasarkan ip client. request hit 10000 per jam
}//function for lazy if off
