function _getCookies(c_name)
{
    var i,x,y,ARRcookies=document.cookie.split(";");
    for (i=0;i<ARRcookies.length;i++)
    {
        x=ARRcookies[i].substr(0,ARRcookies[i].indexOf("="));
        y=ARRcookies[i].substr(ARRcookies[i].indexOf("=")+1);
        x=x.replace(/^\s+|\s+$/g,"");
        if (x==c_name)
        {
            return unescape(y);
        }
    }
    return '';
}

function _setCookies(cookieName, cookieValue, cookiePath, cookieExpires) {
    cookieValue = escape(cookieValue);
    if (cookieExpires == "") {
        var nowDate = new Date();
        /*nowDate.setHours(nowDate.getHours() + 24);*/
        nowDate.setHours(23,55,0,0);
        cookieExpires = nowDate.toGMTString();
    }
    if (cookiePath != "") {
        cookiePath = ";Path=" + cookiePath;
    }
    document.cookie = cookieName + "=" + cookieValue + ";expires=" + cookieExpires + cookiePath + ";Domain=brilio.net";
}